#include "uart.h"
#include <stdio.h>
#include <stdint.h>
#include "osKernel.h"
#include "rom.h"


typedef uint32_t TaskProfiler;
#define QUANTA 1



uint32_t TIM2_Ticks;

#define SLICE_INSTRUCTIONS  100
static void save_stack(task6502_t *t){
  for (int i=0;i<256;i++) t->stack_page[i] = bus_read(0x0100+i);
}

static void load_stack(task6502_t *t){
  for (int i=0;i<256;i++) bus_write(0x0100+i, t->stack_page[i]);
}




void taskIdle(void){
	while(1){
		osThreadYield();
	}
}


void task6502_1(void) {
	for (i = 0; i < SLICE_INSTRUCTIONS; i++) {
	    cpu_step(cpu);
	    if (tcbs[0].state == THREAD_BLOCKED) break;

	}
}

void task6502_2(void) {
	for (i = 0; i < SLICE_INSTRUCTIONS; i++) {
	    cpu_step(cpu);
	    if (tcbs[0].state == THREAD_BLOCKED) break;

	}
}

int main(void){
	//initialize kernel, and add threads

	uart_tx_init();
	tim2_1hz_interrupt_init();
	osSemaphoreInit(&semaphore1, 1);
	osSemaphoreInit(&semaphore2, 0);
	osKernelInit();
	osKernelAddThreads(&task6502, &task_stepper, &taskIdle);
	osKernelLaunch(QUANTA);

}

void TIM2_IRQHandler(void){

	if (TIM2->SR & SR_UIF) {
	    TIM2->SR &= ~SR_UIF;
	    step_count++; //Create one sem token
	    osSemaphoreSet(&sem_step);   // allow exactly one 6502 instruction
	  }
}

